// Creates copy value
inline sdiValue::sdiValue(const sdiValue& other)
    : p_pValuePtr(other.p_pValuePtr ? other.p_pValuePtr->Clone() : 0)
{
}

// To be used by specialized, possibly app-controlled, derived classes of sdiValuePtr.
inline sdiValue::sdiValue(sdiValuePtr* pValuePtr)
    : p_pValuePtr(pValuePtr)
{
}


// Exposed destructor
inline sdiValue::~sdiValue()
{
    sdiValuePtr::Destroy(p_pValuePtr);
}


// Assignment operator
inline sdiValue& sdiValue::operator=(const sdiValue& other)
{
    if (this != &other)
    {
        if (p_pValuePtr)
        {
            sdiValuePtr::Destroy(p_pValuePtr);
        }
        p_pValuePtr = other.p_pValuePtr ? other.p_pValuePtr->Clone() : 0;
    }
    return *this;
}

// Conversion Assignment operator
inline sdiValue& sdiValue::operator=(const sdiValuePtr& other)
{
    if (p_pValuePtr)
    {
        sdiValuePtr::Destroy(p_pValuePtr);
    }
    p_pValuePtr = other.Clone();
    return *this;
}


//! Comparison with other - return false if sdiBasicType, sdiCompoundType, etc. do not match.
inline bool sdiValue::operator==(const sdiValue& other) const
{
    if (!p_pValuePtr && !other.p_pValuePtr)
    {
        return true;
    }

    if (!p_pValuePtr || !other.p_pValuePtr)
    {
        return false;
    }

    return p_pValuePtr->Equals(other.p_pValuePtr);
}
inline bool sdiValue::operator!=(const sdiValue& other) const
{
    return !(*this == other);
}
inline bool sdiValue::operator<(const sdiValue& other) const
{
    if (!p_pValuePtr || !other.p_pValuePtr)
    {
        return false;
    }

    return p_pValuePtr->LessThan(other.p_pValuePtr);
}


