template <class VALUE, sdiBasicType BASIC_TYPE, sdiCompoundType COMPOUND_TYPE>
inline bool sdiValue::P_SetValue(const VALUE& value, const unsigned int i, const unsigned int j)
{
    if (p_pValuePtr)
    {
        if (p_pValuePtr->GetBasicType() == BASIC_TYPE && p_pValuePtr->GetCompoundType() == COMPOUND_TYPE)
        {
            return p_pValuePtr->SetValue(value, i, j);
        }

        sdiValuePtr::Destroy(p_pValuePtr);
    }

    p_pValuePtr = sdiValuePtr::Create(value);
    return true;
}

inline bool sdiValue::SetValue(const bool&           value, const unsigned int i, const unsigned int j)
{
    return P_SetValue<bool, BASIC_TYPE_BOOL, COMPOUND_TYPE_SINGLE>(value, i, j);
}

inline bool sdiValue::SetValue(const char&           value, const unsigned int i, const unsigned int j)
{
    return P_SetValue<char, BASIC_TYPE_CHAR, COMPOUND_TYPE_SINGLE>(value, i, j);
}

