    //! Utility function for derived classed - used to compare all but data
    bool P_EqualTypes(const sdiValuePtr* pOther) const
    {
        //
        // Types must match; assuming that both pointers are  ValuePtrPrivateArray2T*; no no need to compare some things
        //
        if (GetBasicType() != pOther->GetBasicType())
        {
            return false;
        }
        if (GetCompoundType() != pOther->GetCompoundType())
        {
            return false;
        }
        if (GetArrayDimension() != pOther->GetArrayDimension())
        {
            return false;
        }
        if (GetArrayDimensionExtent(0) != pOther->GetArrayDimensionExtent(0))
        {
            return false;
        }
        if (GetArrayDimensionExtent(1) != pOther->GetArrayDimensionExtent(1))
        {
            return false;
        }

        return true;
    }


